changeSet.sql:
CREATE TABLE "user" (
  login VARCHAR(64) PRIMARY KEY NOT NULL,
  avatar_url VARCHAR(2000) NOT NULL,
  created TIMESTAMPTZ DEFAULT now() NOT NULL
);

CREATE TABLE project (
  id SERIAL PRIMARY KEY,
  submitter VARCHAR(64) NOT NULL REFERENCES "user"(login),
  coordinates VARCHAR(64) NOT NULL,
  platform PLATFORM NOT NULL DEFAULT 'github',
  created TIMESTAMPTZ DEFAULT now() NOT NULL
);
CREATE INDEX idx_project1 ON project (id, submitter, coordinates, platform);

CREATE TABLE review (
  id SERIAL PRIMARY KEY,
  project INT NOT NULL REFERENCES project(id),
  author VARCHAR(64) NOT NULL REFERENCES "user"(login),
  text TEXT NOT NULL,
  created TIMESTAMPTZ DEFAULT now() NOT NULL
);
CREATE INDEX idx_review1 ON review (project, author);


CREATE TABLE review_vote (
  id SERIAL PRIMARY KEY,
  review INT NOT NULL REFERENCES review(id),
  "user" VARCHAR(64) NOT NULL REFERENCES "user"(login),
  value BOOLEAN NOT NULL DEFAULT null,
  UNIQUE(review, "user"),
  created TIMESTAMPTZ DEFAULT now() NOT NULL
);
CREATE INDEX idx_review_vote1 ON review_vote (review, "user");

CREATE TABLE badge (
  id SERIAL PRIMARY KEY,
  project INT NOT NULL REFERENCES project(id),
  text VARCHAR(64) NOT NULL,
  created TIMESTAMPTZ DEFAULT now() NOT NULL
);
CREATE INDEX idx_badge1 ON badge (project);
