changeSet.sql:
CREATE TABLE "user" (
  id SERIAL PRIMARY KEY,
  login VARCHAR(64) NOT NULL,
  avatar_url VARCHAR(100) NOT NULL,
  created TIMESTAMPTZ DEFAULT now() NOT NULL
);
CREATE INDEX idx_user1 ON "user" (login);

CREATE TABLE review (
  id SERIAL PRIMARY KEY,
  project INT NOT NULL REFERENCES project(id),
  submitter INT NOT NULL REFERENCES "user"(id),
  text VARCHAR(500) NOT NULL,
  vote_count INT NOT NULL DEFAULT 0,
  created TIMESTAMPTZ DEFAULT now() NOT NULL,
  edited TIMESTAMPTZ DEFAULT now() NOT NULL
);
CREATE INDEX idx_review1 ON review (project, submitter, text);
